(44976, 1, '2014-05-19 16:07:57', '2014-05-19 22:07:57', '/* \n    This is to get rid of the background image and to change the background color\n    to white since this is not an option in the Willbridge theme editor \n*/\nbody {\n    background-image: none !important;\n    background-color: #fff;\n}\n\n/*\n    This is to change the background of the navigation  tags to the brown color\n    instead of the image that is a shadow by default with the Willbridge theme.\n*/\n#navigation > li > a > span {\n    background-image: none !important;\n    background-color: #503A37;\n}\n\n/*\n    These next few rules are to change the color at the bottom of the navigation\n    links according to the style guide.  They each have their own rule because\n    they each have their own color.  If they all had one color, there wouldn''t be\n    a need to have these.\n*/\n#navigation > li:first-child > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:first-child > a:hover, \n#navigation > li:hover:first-child > a {\n    background-color: #8EB8C8;\n}\n\n#navigation > li:nth-child(2) > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(2) > a:hover, \n#navigation > li:hover:nth-child(2) > a{\n    background-color: #EBAD21;\n}\n\n#navigation > li:nth-child(3) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(3) > a:hover, \n#navigation > li:hover:nth-child(3) > a{\n    background-color: #A9B437;\n}\n\n#navigation > li:nth-child(4) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(4) > a:hover, \n#navigation > li:hover:nth-child(4) > a{\n    background-color: #B33A41;\n}\n\n#navigation > li:nth-child(5) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(5) > a:hover, \n#navigation > li:hover:nth-child(5) > a{\n    background-color: #8EB8C8;\n}\n/* End of color changes for the navigation links, started on line 25 */\n\n/* This is to change the footer area so that the BYU-I logo is shown correctly. */\n#footer {\n    height: 100px !important;\n}\n\n.footer_inner {\n    height: 40px !important;\n}', 'Custom CSS', 'custom-css-1400537277.min.css', 'inherit', 'open', 'open', '', '44951-revision-v1', '', '', '2014-05-19 16:07:57', '2014-05-19 22:07:57', '', 44951, 'http://localhost/ldp/htdocs/44951-revision-v1/', 0, 'revision', '', 0),
(44977, 1, '2014-05-19 16:47:40', '2014-05-19 22:47:40', '/* \n    This is to get rid of the background image and to change the background color\n    to white since this is not an option in the Willbridge theme editor \n*/\nbody {\n    background-image: none !important;\n    background-color: #fff;\n}\n\n/*\n    This is to change the background of the navigation  tags to the brown color\n    instead of the image that is a shadow by default with the Willbridge theme.\n*/\n#navigation > li > a > span {\n    background-image: none !important;\n    background-color: #503A37;\n}\n\n/*\n    These next few rules are to change the color at the bottom of the navigation\n    links according to the style guide.  They each have their own rule because\n    they each have their own color.  If they all had one color, there wouldn''t be\n    a need to have these.\n*/\n#navigation > li:first-child > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:first-child > a:hover, \n#navigation > li:hover:first-child > a {\n    background-color: #8EB8C8;\n}\n\n#navigation > li:nth-child(2) > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(2) > a:hover, \n#navigation > li:hover:nth-child(2) > a{\n    background-color: #EBAD21;\n}\n\n#navigation > li:nth-child(3) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(3) > a:hover, \n#navigation > li:hover:nth-child(3) > a{\n    background-color: #A9B437;\n}\n\n#navigation > li:nth-child(4) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(4) > a:hover, \n#navigation > li:hover:nth-child(4) > a{\n    background-color: #B33A41;\n}\n\n#navigation > li:nth-child(5) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(5) > a:hover, \n#navigation > li:hover:nth-child(5) > a{\n    background-color: #8EB8C8;\n}\n/* End of color changes for the navigation links, started on line 25 */\n\n/* This is to change the footer area so that the BYU-I logo is shown correctly. */\n#footer {\n    height: 110px !important;\n}\n\n.footer_inner {\n    height: 50px !important;\n}\n/* End of footer hieght size changes */\n\n/* Changing the margins for the Social media and BYUI logo */\n.social_lis {\n    float: left;\n}\n\n.social_list li {\n    margin-right: 10px;\n}\n\n#BYUI_logo {\n    float: right;\n    display: block;\n    padding-left: 10px;\n}\n/* End of the social media margins changes */', 'Custom CSS', 'custom-css-1400539660.min.css', 'inherit', 'open', 'open', '', '44951-revision-v1', '', '', '2014-05-19 16:47:40', '2014-05-19 22:47:40', '', 44951, 'http://localhost/ldp/htdocs/44951-revision-v1/', 0, 'revision', '', 0),
(44978, 1, '2014-05-19 16:48:46', '2014-05-19 22:48:46', '/* \n    This is to get rid of the background image and to change the background color\n    to white since this is not an option in the Willbridge theme editor \n*/\nbody {\n    background-image: none !important;\n    background-color: #fff;\n}\n\n/*\n    This is to change the background of the navigation  tags to the brown color\n    instead of the image that is a shadow by default with the Willbridge theme.\n*/\n#navigation > li > a > span {\n    background-image: none !important;\n    background-color: #503A37;\n}\n\n/*\n    These next few rules are to change the color at the bottom of the navigation\n    links according to the style guide.  They each have their own rule because\n    they each have their own color.  If they all had one color, there wouldn''t be\n    a need to have these.\n*/\n#navigation > li:first-child > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:first-child > a:hover, \n#navigation > li:hover:first-child > a {\n    background-color: #8EB8C8;\n}\n\n#navigation > li:nth-child(2) > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(2) > a:hover, \n#navigation > li:hover:nth-child(2) > a{\n    background-color: #EBAD21;\n}\n\n#navigation > li:nth-child(3) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(3) > a:hover, \n#navigation > li:hover:nth-child(3) > a{\n    background-color: #A9B437;\n}\n\n#navigation > li:nth-child(4) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(4) > a:hover, \n#navigation > li:hover:nth-child(4) > a{\n    background-color: #B33A41;\n}\n\n#navigation > li:nth-child(5) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(5) > a:hover, \n#navigation > li:hover:nth-child(5) > a{\n    background-color: #8EB8C8;\n}\n/* End of color changes for the navigation links, started on line 25 */\n\n/* This is to change the footer area so that the BYU-I logo is shown correctly. */\n#footer {\n    height: 110px !important;\n}\n\n.footer_inner {\n    height: 50px !important;\n}\n/* End of footer hieght size changes */\n\n/* Changing the margins for the Social media and BYUI logo */\n.social_list {\n    float: left;\n}\n\n.social_list li {\n    margin-right: 10px;\n}\n\n#BYUI_logo {\n    float: right;\n    display: block;\n    padding-left: 10px;\n}\n/* End of the social media margins changes */', 'Custom CSS', 'custom-css-1400539726.min.css', 'inherit', 'open', 'open', '', '44951-revision-v1', '', '', '2014-05-19 16:48:46', '2014-05-19 22:48:46', '', 44951, 'http://localhost/ldp/htdocs/44951-revision-v1/', 0, 'revision', '', 0),
(44979, 1, '2014-05-19 16:55:54', '2014-05-19 22:55:54', '/* \n    This is to get rid of the background image and to change the background color\n    to white since this is not an option in the Willbridge theme editor \n*/\nbody {\n    background-image: none !important;\n    background-color: #fff;\n}\n\n/*\n    This is to change the background of the navigation  tags to the brown color\n    instead of the image that is a shadow by default with the Willbridge theme.\n*/\n#navigation > li > a > span {\n    background-image: none !important;\n    background-color: #503A37;\n}\n\n/*\n    These next few rules are to change the color at the bottom of the navigation\n    links according to the style guide.  They each have their own rule because\n    they each have their own color.  If they all had one color, there wouldn''t be\n    a need to have these.\n*/\n#navigation > li:first-child > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:first-child > a:hover, \n#navigation > li:hover:first-child > a {\n    background-color: #8EB8C8;\n}\n\n#navigation > li:nth-child(2) > a, \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(2) > a:hover, \n#navigation > li:hover:nth-child(2) > a{\n    background-color: #EBAD21;\n}\n\n#navigation > li:nth-child(3) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(3) > a:hover, \n#navigation > li:hover:nth-child(3) > a{\n    background-color: #A9B437;\n}\n\n#navigation > li:nth-child(4) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(4) > a:hover, \n#navigation > li:hover:nth-child(4) > a{\n    background-color: #B33A41;\n}\n\n#navigation > li:nth-child(5) > a , \n#navigation > li.current-menu-ancestor > a, \n#navigation > li:hover:nth-child(5) > a:hover, \n#navigation > li:hover:nth-child(5) > a{\n    background-color: #8EB8C8;\n}\n/* End of color changes for the navigation links, started on line 25 */\n\n/* This is to change the footer area so that the BYU-I logo is shown correctly. */\n#footer {\n    height: 110px !important;\n}\n\n.footer_inner {\n    height: 50px !important;\n}\n/* End of footer hieght size changes */\n\n/* Changing the margins for the Social media and BYUI logo */\n.social_list {\n    float: left;\n}\n\n.social_list li {\n    margin-right: 10px;\n}\n\n#BYUI_logo {\n    float: right;\n    display: block;\n    padding-left: 10px;\n    border-left-style: solid;\n    border-color: #503a37;\n    border-width: 1px;\n}\n/* End of the social media margins changes */', 'Custom CSS', 'custom-css-1400540154.min.css', 'inherit', 'open', 'open', '', '44951-revision-v1', '', '', '2014-05-19 16:55:54', '2014-05-19 22:55:54', '', 44951, 'http://localhost/ldp/htdocs/44951-revision-v1/', 0, 'revision', '', 0);
